import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const navigate = useNavigate();
  const [showDropdown, setShowDropdown] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.reload();
  };

  const toggleDropdown = () => {
    setShowDropdown(prev => !prev);
  };

  const closeDropdown = (event) => {
    if (showDropdown && event.target.closest('.dropdown') === null) {
      setShowDropdown(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', closeDropdown);
    return () => {
      document.removeEventListener('click', closeDropdown);
    };
  }, [showDropdown]);

  return (
    <div className="dropdown">
      <button
        className="btn rounded-circle"
        onClick={toggleDropdown}
        style={{
          backgroundColor: 'transparent',
          border: 'none',
          cursor: 'pointer',
          padding: '0',
          marginRight: '10px',
        }}
        id="profileDropdown"
        aria-expanded={showDropdown}
      >
        {/* <img
          src="https://media.istockphoto.com/id/1332100919/vector/man-icon-black-icon-person-symbol.jpg?s=612x612&w=0&k=20&c=AVVJkvxQQCuBhawHrUhDRTCeNQ3Jgt0K1tXjJsFy1eg="
          alt="Profile"
          style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            objectFit: 'cover',
          }}
        /> */}
      </button>
      {showDropdown && (
        <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="profileDropdown">
          <li>
            <button className="dropdown-item" onClick={() => navigate('/my-orders')}>My Orders</button>
          </li>
          <li>
            <button className="dropdown-item" onClick={() => navigate('/change-password')}>Change Password</button>
          </li>
          <li>
            <button className="dropdown-item" onClick={() => navigate('/update-email')}>Update Email</button>
          </li>
          <li>
            <button className="dropdown-item" onClick={() => navigate('/update-phone')}>Update Phone Number</button>
          </li>
          <li>
            <button className="dropdown-item" onClick={() => navigate('/support')}>Support</button>
          </li>
          <li>
            <button className="dropdown-item" onClick={handleLogout}>Logout</button>
          </li>
        </ul>
      )}
    </div>
  );
}

export default Profile;
