import React, { useState } from 'react';
import axios from 'axios';

const UpdatePhone = ({ darkMode, showPhoneModal, setShowPhoneModal }) => {
  const [phone, setPhone] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);

  const closeModal = () => {
    setShowPhoneModal(false);
    setPhone('');
    setOtp('');
    setOtpSent(false);
  };

  const requestOtp = async () => {
    try {
      const response = await axios.post('/api/twilio_sendotp', { phone });
      if (response.data.success) {
        setOtpSent(true);
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("Error requesting OTP:", error);
    }
  };

  const verifyOtp = async () => {
    try {
      const response = await axios.post('/api/twilio_verifyotp', { phone, otp });
      if (response.data.success) {
        alert('Phone number verified successfully.');
        closeModal();
      } else {
        alert('OTP verification failed. Please try again.');
      }
    } catch (error) {
      console.error("Error verifying OTP:", error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!otpSent) {
      requestOtp();
    } else {
      verifyOtp();
    }
  };

  return (
    <>
      {showPhoneModal && (
        <div className="modal-overlay">
          <div className={`modal-content ${darkMode ? 'dark' : ''}`}>
            <h2>Update Phone Number</h2>
            <form onSubmit={handleSubmit}>
              <label>
                New Phone Number:
                <input
                  type="tel"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  required
                  disabled={otpSent}
                />
              </label>
              {otpSent && (
                <label>
                  Enter OTP:
                  <input
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    required
                  />
                </label>
              )}
              <button type="submit">{otpSent ? "Verify OTP" : "Request OTP"}</button>
              <button type="button" onClick={closeModal}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default UpdatePhone;
