// Signup.jsx

import React, { useState } from 'react';

function Signup({ showModal, closeModal, openLoginModal }) {
  const [credentials, setCredentials] = useState({ name: "", email: "", password: "", geolocation: "" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle signup functionality here (e.g., create a new user)
    alert("Signup successful");
    closeModal();
    openLoginModal();
  };

  const onChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  return (
    <>
      {showModal && (
        <div className="modal fade show" style={{ display: 'block' }} aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Signup</h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      value={credentials.name}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email address</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={credentials.email}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input
                      type="password"
                      className="form-control"
                      name="password"
                      value={credentials.password}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="geolocation" className="form-label">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      name="geolocation"
                      value={credentials.geolocation}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary m-3">Submit</button>
                  <button type="button" className="btn btn-danger m-3" onClick={openLoginModal}>Already a user</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Signup;
