import React, { useState } from 'react';  // Added useState import
import { useLocation, useNavigate } from 'react-router-dom';  // Added useHistory import

function ChooseUPI() {
  const location = useLocation();
  const navigate = useNavigate();
  const { orderDetails } = location.state||{};

  const [transactionId, setTransactionId] = useState('');
  const [showTransactionBox, setShowTransactionBox] = useState(false);
  const [error, setError] = useState('');

  if (!orderDetails) {
    return <div>Error: No order details available</div>;
  }


  const total = orderDetails.total;

  const googlePayLink = `https://pay.google.com/gp/p/ui/pay?pa=manoj7799076555@okicici&pn=manoj%20siva&tn=Payment%20for%20Order&am=${total}&cu=INR`;
  const phonePeLink = `https://upi.phonepe.com/pay?pa=yourname@ybl&pn=Your%20Name&tn=Payment%20for%20Order&am=${total}&cu=INR`;

  const handleLinkClick = () => {
    setShowTransactionBox(true);
  };

  const handleTransactionSubmit = async () => {
    if (transactionId.trim() === '') {
      setError('Transaction ID is required');
      return;
    }

    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderId: orderDetails._id,
          transactionId: transactionId.trim(),
          totalAmount: orderDetails.total,
          items: orderDetails.items,
          status: 'Pending', // Initial status is pending
        }),
      });

      if (response.ok) {
        navigate(`/verify/${orderDetails._id}`);
      } else {
        setError('Failed to submit transaction details.');
      }
    } catch (err) {
      setError('Error submitting transaction details.');
    }
  };

  return (
    <div className="container mt-5">
      <h3>Pay Using UPI</h3>
      <p>Total Amount: â‚¹{total}</p>

      <div className="mt-4">
        {/* Google pay button */}
        <a
          href={googlePayLink}
          className="btn btn-outline-primary me-3"
          target="_blank"
          rel="noopener noreferrer"
          onClick={handleLinkClick}  // Ensure link click triggers transaction box
        >
          Pay with Google Pay
        </a>

        <a
          href={phonePeLink}
          className="btn btn-outline-primary me-3"
          target="_blank"
          rel="noopener noreferrer"
          onClick={handleLinkClick}  // Ensure link click triggers transaction box
        >
          Pay with PhonePe
        </a>
      </div>

      {showTransactionBox && (
        <div className="mt-5">
          <h5>Enter your Transaction ID:</h5>
          <input
            type="text"
            value={transactionId}
            onChange={(e) => setTransactionId(e.target.value)}
            className="form-control mb-2"
            placeholder="Enter transaction ID"
          />
          {error && <p className="text-danger">{error}</p>}
          <button className="btn btn-success" onClick={handleTransactionSubmit}>Submit</button>  {/* Fixed typo in onClick */}
        </div>
      )}
    </div>
  );
}

export default ChooseUPI;
