import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import Login from '../screens/Login';

function Navbar() {

  const [darkMode,setDarkMode]=useState(false);
  const location=useLocation();

  useEffect(()=>{
    const savedTheme = localStorage.getItem('theme');
    if(savedTheme==='light')
    {
      setDarkMode(false);
      document.body.classList.add('light-mode');
    }
  },[]);

  const toggleTheme = () => {
    if (darkMode) {
      document.body.classList.remove('dark-mode');
      document.body.classList.add('light-mode');
      setDarkMode(false);
      localStorage.setItem('theme', 'light');
    } else {
      document.body.classList.remove('light-mode');
      document.body.classList.add('dark-mode');
      setDarkMode(true);
      localStorage.setItem('theme', 'dark');
    }
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-success">
        <div className="container-fluid">
          <NavLink className="navbar-brand fs-1 fst-italic d-flex align-items-center" to="/">
            {/* Logo Image */}
            <img 
              src="logo1.jpg" 
              alt="Logo" 
              className="logo-image"
            />
            <b>Ice and Fire</b>
          </NavLink>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">

              <li className="nav-item">
                <NavLink className={({isActive})=>(isActive ?"nav-link active":"nav-link")} aria-current="page" to="/">Home</NavLink>
              </li>

              <li className="nav-item">
              <NavLink className="nav-link" to="/loginuser" style={{ textDecoration: 'none' }}>Login</NavLink>
              </li>

              <li className="nav-item">
                <NavLink className={({isActive})=>(isActive?"nav-link active":"nav-link")} to="/Signup">Signup</NavLink>
              </li>
            </ul>
            
            <div>
              <button className="btn btn-light ms-auto" onClick={toggleTheme}>
                {darkMode ?'Light Mode':'Dark Mode'}
              </button>
            </div>

          </div>
        </div>
      </nav>
      
      {/* Modal structure */}
      {location.pathname==='/loginuser' &&(
      <div className="modal fade show" id="loginModal" tabIndex="-1" aria-labelledby="loginModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="loginModalLabel">Login</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <Login/>
            </div>
          </div>
        </div>
      </div>
      )}

    </>
  );
}

export default Navbar;
