import React, { useState } from 'react';

function ChangePassword({ darkMode, showPassModal, setShowPassModal }) {
  const [credentials, setCredentials] = useState({
    oldpassword: "",
    newpassword1: "",
    newpassword2: ""
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (credentials.newpassword1 !== credentials.newpassword2) {
      alert("New passwords do not match.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/changePassword", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          oldpassword: credentials.oldpassword,
          newpass
        })
      });

      const json = await response.json();
      if (json.success) {
        alert("Password updated successfully.");
        setShowPassModal(false);
      } else {
        alert("Old password is incorrect or update failed.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while updating the password.");
    }
  };

  const onChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  return (
    <>
      {showPassModal && (
        <div
          className="modal fade show"
          tabIndex="-1"
          role="dialog"
          style={{ display: 'block' }}
          aria-hidden="true"
        >
          <div className="modal-dialog" role="document">
            <div className={`modal-content ${darkMode ? 'bg-dark text-light' : 'bg-light text-dark'}`}>
              <div className="modal-header">
                <h5 className="modal-title">Update Password</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowPassModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="exampleInputOldPass" className="form-label">Enter old password</label>
                    <input
                      type="password"
                      className="form-control"
                      id="exampleInputOldPass"
                      name="oldpassword"
                      value={credentials.oldpassword}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="exampleInputNewPassword1" className="form-label">Enter new password</label>
                    <input
                      type="password"
                      className="form-control"
                      id="exampleInputNewPassword1"
                      name="newpassword1"
                      value={credentials.newpassword1}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="exampleInputNewPassword2" className="form-label">Confirm new password</label>
                    <input
                      type="password"
                      className="form-control"
                      id="exampleInputNewPassword2"
                      name="newpassword2"
                      value={credentials.newpassword2}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <button type="submit" className="m-3 btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default ChangePassword;
