import React, { useState } from "react";
import Login from './Login';

function Signup({ darkMode }) {
  const [credentials, setCredentials] = useState({
    name: "",
    email: "",
    password: "",
    geolocation: ""
  });
  const [showModal, setShowModal] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/api/createuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: credentials.name,
          email: credentials.email,
          password: credentials.password,
          location: credentials.geolocation
        })
      });

      const json = await response.json();
      if (json.success) {
        alert("Signup successful. Now login with your credentials");
        setCredentials({ email: "", password: "", name: "", geolocation: "" });
        setShowModal(false);
      } else {
        alert("Enter valid credentials");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const onChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  const closeModal = () => setShowModal(false);

  const handleLogin = () => {
    if (showModal) closeModal();
    <Login />;
  };

  return (
    <>
      <button onClick={() => setShowModal(true)} className="btn btn-link" style={{ textDecoration: 'none', color: 'white' }}>
        Signup
      </button>

      {showModal && (
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }} aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className={`modal-content ${darkMode ? 'bg-dark text-light' : 'bg-light text-dark'}`}>
              <div className="modal-header">
                <h5 className="modal-title">Signup</h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      value={credentials.name}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email address</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={credentials.email}
                      onChange={onChange}
                      aria-describedby="emailHelp"
                      required
                    />
                    <div id="emailHelp" className="form-text">
                      We'll never share your email with anyone else.
                    </div>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input
                      type="password"
                      className="form-control"
                      name="password"
                      value={credentials.password}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="geolocation" className="form-label">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      name="geolocation"
                      value={credentials.geolocation}
                      onChange={onChange}
                      required
                    />
                  </div>
                  <button type="submit" className="m-3 btn btn-primary">Submit</button>
                  <button type="button" className="m-3 btn btn-danger" onClick={handleLogin}>Already a user</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Signup;
