import React from 'react';
import { useCart, useDispatch } from '../components/ContextReducer';


function Cart({ darkMode, showCartModal, setShowCartModal }) {
  const cartItems = useCart();
  const dispatch = useDispatch();

  const closeModal = () => {
    setShowCartModal(false);
  };

  const handleDelete = (id) => {
    dispatch({
      type: "REMOVE_FROM_CART",
      payload: id
    });
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <>
      {showCartModal && (
        <div className={`modal ${darkMode ? 'dark-mode' : ''}`} style={{ display: 'block' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">My Cart</h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                {cartItems.length > 0 ? (
                  <ul className="list-group">
                    {cartItems.map((item, index) => (
                      <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                        <span>
                          {item.name} - Quantity: {item.quantity} - Price: ${(item.price * item.quantity).toFixed(2)}
                        </span>
                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(item.id)}>Delete</button>
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p>Your cart is empty.</p>
                )}
                <h5 className="mt-3">Total: ${calculateTotal()}</h5>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>Close</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Cart;
