import React, { useState } from 'react';
import axios from 'axios';

const UpdatePhone = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [phone, setPhone] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [verificationSuccess, setVerificationSuccess] = useState(false);

  const openModal = () => setIsOpen(true);
  const closeModal = () => {
    setIsOpen(false);
    resetForm();
  };

  const resetForm = () => {
    setPhone('');
    setOtp('');
    setOtpSent(false);
    setVerificationSuccess(false);
  };

  const handlePhoneChange = (e) => {
    setPhone(e.target.value);
  };

  const handleOtpChange = (e) => {
    setOtp(e.target.value);
  };

  const requestOtp = async () => {
    try {
      const response = await axios.post('/api/twilio_sendotp', { phone });
      if (response.data.success) {
        setOtpSent(true);
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("Error requesting OTP:", error);
    }
  };

  const verifyOtp = async () => {
    try {
      const response = await axios.post('/api/twilio_verifyotp', { phone, otp });
      if (response.data.success) {
        setVerificationSuccess(true);
        closeModal();
        alert('Phone number verified successfully.');
      } else {
        alert('OTP verification failed. Please try again.');
      }
    } catch (error) {
      console.error("Error verifying OTP:", error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent form submission
    if (!otpSent) {
      requestOtp();
    } else {
      verifyOtp();
    }
  };

  return (
    <div>
      <button type="button" onClick={openModal}>Update Phone Number</button> {/* Ensure type is button */}
      
      {isOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Update Phone Number</h2>
            <form onSubmit={handleSubmit}>
              <label>
                New Phone Number:
                <input
                  type="tel"
                  value={phone}
                  onChange={handlePhoneChange}
                  placeholder="Enter new phone number"
                  required
                  disabled={otpSent}
                />
              </label>
              
              {otpSent && (
                <label>
                  Enter OTP:
                  <input
                    type="text"
                    value={otp}
                    onChange={handleOtpChange}
                    placeholder="Enter OTP"
                    required
                  />
                </label>
              )}
              
              <button type="submit">
                {otpSent ? "Verify OTP" : "Request OTP"}
              </button>
              <button type="button" onClick={closeModal}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UpdatePhone;
