import React from "react";

function Card({title, image, description, price}) {
  
  const imageUrl = `/${image}`;

  return (
    <div className="card mt-1 mb-2" style={{ width: "22rem", maxHeight: "500px" }}>
      <img src={imageUrl} className="card-img-top" alt={title} style={{ height: "250px", width: "100%", objectFit: "cover" }} />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{description}</p>
        <div className="container w-100">
          <select className="m-2 h-100 bg-success">
            {Array.from(Array(10), (e, i) => (
              <option key={i + 1} value={i + 1}>{i + 1}</option>
            ))}
          </select>
          <div className="d-inline h-100 fs-5"><b>Total :{price}</b></div>
        </div>
      </div>
    </div>
  );
}

export default Card;
