import React from 'react';

function Profile() {
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.reload();
  };

  return (
    <div className="dropdown">
      <button
        className="btn rounded-circle"
        onClick={toggleDropdown}
        style={{
          backgroundColor: 'transparent',
          border: 'none',
          cursor: 'pointer',
          padding: '0',
          marginRight: '10px',
        }}
        id="profileDropdown"
        aria-expanded={showDropdown}
      >
        <img
          src="https://media.istockphoto.com/id/1332100919/vector/man-icon-black-icon-person-symbol.jpg?s=612x612&w=0&k=20&c=AVVJkvxQQCuBhawHrUhDRTCeNQ3Jgt0K1tXjJsFy1eg="
          alt="Profile"
          style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            objectFit: 'cover',
          }}
        />
      </button>
      <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
        <li><a className="dropdown-item" href="/my-orders">My Orders</a></li>
        <li><a className="dropdown-item" href="/change-password">Change Password</a></li>
        <li><a className="dropdown-item" href="/update-email">Update Email</a></li>
        <li><a className="dropdown-item" href="/update-phone">Update Phone Number</a></li>
        <li><a className="dropdown-item" href="/support">Support</a></li>
        <li><button className="dropdown-item" onClick={handleLogout}>Logout</button></li>
      </ul>
    </div>
  );
}

export default Profile;
