import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Card from "../components/Cards/Card";
import MisalPav from "../components/Cards/Misalpav";
import VegSand from "../components/Cards/Veg-Sandwhich";
import PaneerSand from "../components/Cards/Panner-Sandwhich";
import Carousal from "../components/Carousal";

function Home() {
  const [search, setSearch] = useState('');
  const [foodItems, setFoodItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFoodItems = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/food-item-names');
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const names = await response.json();
        setFoodItems(names);
      } catch (error) {
        setError(error); // Set error state if fetching fails
        console.error("Error fetching food items:", error);
      }
    };
    fetchFoodItems();
  }, []);

  const filteredItems = foodItems.filter(item =>
    item && item.toLowerCase().includes(search.toLowerCase()) // Check for null values
  );

  if (error) {
    return <div>Error loading items: {error.message}</div>; // Use the error state
  }

  return (
    <div>
      <Carousal setSearch={setSearch} />
      <div className="container m-3 d-flex flex-wrap">
        <div className="flex-fill me-3">
          {filteredItems.map((item, index) => (
            <Card key={index} name={item} /> // Pass filtered item name as a prop to Card
          ))}
          <MisalPav />
        </div>
        <div className="flex-fill ms-3">
          <PaneerSand />
          <VegSand />
        </div>
      </div>
      <div><Footer /></div>
    </div>
  );
}

export default Home;
