// Signup.jsx

import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

function Signup() {
  const [credentials, setCredentials] = useState({
    name: "",
    email: "",
    password: "",
    geolocation: ""
  });

  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);

  const openModal = () => setShowModal(true);
  const closeModal = () => setShowModal(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/api/createuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: credentials.name,
          email: credentials.email,
          password: credentials.password,
          location: credentials.geolocation
        })
      });

      const json = await response.json();
      console.log(json);

      if (json.success) {
        alert("Signup successful. Now login with your credentials");
        closeModal();
        navigate('/loginuser');
      } else {
        alert("Enter valid credentials");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const onChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  const handleLogin = () => {
    navigate('/loginuser');
    if (showModal) closeModal();
  };

  return (
    <>
      {/* Button to open the modal */}
      <button onClick={openModal} className="btn btn-primary">Signup</button>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }} aria-hidden="true">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Signup</h5>
                  <button type="button" className="btn-close" onClick={closeModal}></button>
                </div>
                <div className="modal-body">
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label htmlFor="name" className="form-label">Name</label>
                      <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={credentials.name}
                        onChange={onChange}
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                      <input
                        type="email"
                        className="form-control"
                        id="exampleInputEmail1"
                        name="email"
                        value={credentials.email}
                        onChange={onChange}
                        aria-describedby="emailHelp"
                        required
                      />
                      <div id="emailHelp" className="form-text">
                        We'll never share your email with anyone else.
                      </div>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                      <input
                        type="password"
                        className="form-control"
                        id="exampleInputPassword1"
                        name="password"
                        value={credentials.password}
                        onChange={onChange}
                        required
                      />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="geolocation" className="form-label">Address</label>
                      <input
                        type="text"
                        className="form-control"
                        id="geolocation"
                        name="geolocation"
                        value={credentials.geolocation}
                        onChange={onChange}
                        required
                      />
                    </div>
                    <button type="submit" className="m-3 btn btn-primary">Submit</button>
                    <button type="button" className="m-3 btn btn-danger" onClick={handleLogin}>Already a user</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Signup;
