import React ,{useState} from "react";
import {Link} from "react-router-dom";


function Signup() {

  const [credentials,setCredentials]=useState({name:"",emai:"",password:"",geolocation:""})

  const handleSubmit=async(e)=>{
      e.preventDefault();
      const response= await fetch("http://localhost:5000/api/createuser",{
        mehtod:'POST',
        heder:{
          'Content-type':'application/json'
        },
        body:JSON.stringify({name:credentials.name, email:credentials.email, password:credentials.password, location:credentials.geolocation  })
      });
      const json=await response.json()
      console.log(json);

      if(json.success){
        alert("Enter Valid Credentials")
      }
  }
const onChange=(event)=>{
  setCredentials({...credentials,[event.target.name]:event.target.value})
}
  return (
    <>
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label for="name" className="form-label" name='name' value={credentials.name} onChange={onChange}>
              Name
            </label>
            <input type="email" className="form-control" />
          </div>
          <div className="mb-3">
            <label for="exampleInputEmail1" className="form-label" name='email' value={credentials.email} onChnage={onChange}>
              Email address
            </label>
            <input
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
            <div id="emailHelp" className="form-text">
              We'll never share your email with anyone else.
            </div>
          </div>
          <div className="mb-3">
            <label for="exampleInputPassword1" className="form-label" name="password" value={credentials.password} onChange={onChange}>
              Password
            </label>
            <input
              type="password"
              className="form-control"
              id="exampleInputPassword1"
            />

<label for="exampleInputPassword1" className="form-label" name="geolocation" value={credentials.geolocation} onChange={onChange}>
              Address
            </label>
            <input
              type="address"
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <button type="submit" className="m-3 btn btn-primary">
            Submit
          </button>
          <Link to="/login" className='m-3 btn btn-danger'>Already a user</Link>
        </form>
      </div>
    </>
  );
}

export default Signup;
